def test_in_memory_loader():
    id = "test"
    tx: TxData = {
        "hash": "0xb8fbee3430ed8cfb8793407b61c4d801e61b48c08123ceaed4137643aa9c79a6",
        "from": "0x8591204047dc7d6edc782fa3cc8ee29e2bdd61e5",
        "to": "0xdef171fe48cf0115b1d80b88dc8eab59176fee57",
        "input": "",
        "value": "0x0",
    }

    with InMemoryLoader(
        id,
        tx,
        data_normal=vm_trace,
        data_reverse=vm_trace,
        parser=VmTraceEventsParser(),
    ) as bundle:
        assert bundle.id == id

        assert (
            bundle.tx_victim.hash.with_prefix()
            == "0xb8fbee3430ed8cfb8793407b61c4d801e61b48c08123ceaed4137643aa9c79a6"
        )
        assert (
            bundle.tx_victim.caller.with_prefix()
            == "0x8591204047dc7d6edc782fa3cc8ee29e2bdd61e5"
        )
        assert (
            bundle.tx_victim.to.with_prefix()
            == "0xdef171fe48cf0115b1d80b88dc8eab59176fee57"
        )
        assert (
            bundle.tx_victim.calldata
            == ""
        )
        assert bundle.tx_victim.value.as_int() == 0

        assert len(list(bundle.tx_victim.events_normal)) == 13
        assert len(list(bundle.tx_victim.events_reverse)) == 13
